import "./register.css"
import { useNavigate } from "react-router-dom";
import { createUserWithEmailAndPassword } from "firebase/auth";
import { useState } from "react";
import { auth } from "../../../firebase";
import { validatePassword } from "../../../utils/passwordHelpers";

export default function Register() {
  const navigate = useNavigate();

  const [username, setUsername] = useState("");
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [error, setError] = useState("");
  const [isRegistering, setIsRegistering] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");

    // 验证所有必填字段
    if (!username.trim() || !firstName.trim() || !lastName.trim()) {
      setError("All fields are required");
      return;
    }

    // 验证用户名格式
    if (!/^[a-zA-Z0-9_]{3,20}$/.test(username)) {
      setError("Username must be 3-20 characters and can only contain letters, numbers and underscore");
      return;
    }

    // 验证密码
    if (password !== confirmPassword) {
      setError("Passwords do not match");
      return;
    }

    const passwordError = validatePassword(password);
    if (passwordError) {
      setError(passwordError);
      return;
    }

    try {
      setIsRegistering(true);
      // 1. 先在 Firebase 创建用户
      const userCredential = await createUserWithEmailAndPassword(auth, email, password);

      // 2. 然后将用户数据保存到 MongoDB
      const response = await fetch('http://localhost:5001/api/users/register', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          firebaseUid: userCredential.user.uid,
          username,
          firstName,
          lastName,
        }),
      });

      // 添加这些日志
      console.log('Response status:', response.status);
      const responseData = await response.json();
      console.log('Response data:', responseData);

      if (!response.ok) {
        throw new Error(responseData.message || 'Failed to save user data');
      } else {
        navigate("/login");
      }
    } catch (error) {
      setIsRegistering(false);
      // 添加更详细的错误日志
      console.error('Registration error:', error);

      if (error.code) {
        // Firebase 错误
        switch (error.code) {
          case 'auth/email-already-in-use':
            setError("Email already exists");
            break;
          case 'auth/invalid-email':
            setError("Invalid email format");
            break;
          default:
            setError(`Registration failed: ${error.message}`);
        }
      } else {
        // MongoDB 或其他错误
        setError(error.message || "Registration failed. Please try again.");
      }
    }
  };

  return (
    <div className="register">
      <span className="registerTitle">Register</span>
      <form className="registerForm" onSubmit={handleSubmit}>
        <label>Username</label>
        <input
          type="text"
          className="registerInput"
          placeholder="Enter your username..."
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          autoFocus={true}
          minLength={3}
          maxLength={30}
        />

        <label>First Name</label>
        <input
          type="text"
          className="registerInput"
          placeholder="Enter your first name..."
          value={firstName}
          onChange={(e) => setFirstName(e.target.value)}
        />

        <label>Last Name</label>
        <input
          type="text"
          className="registerInput"
          placeholder="Enter your last name..."
          value={lastName}
          onChange={(e) => setLastName(e.target.value)}
        />

        <label>Email</label>
        <input
          type="email"
          className="registerInput"
          placeholder="Enter your email..."
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />

        <label>Password</label>
        <input
          type="password"
          className="registerInput"
          placeholder="Enter your password..."
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />

        <label>Confirm Password</label>
        <input
          type="password"
          className="registerInput"
          placeholder="Confirm your password..."
          value={confirmPassword}
          onChange={(e) => setConfirmPassword(e.target.value)}
        />

        <button 
          className="registerButton" 
          type="submit"
          disabled={isRegistering}
        >
          {isRegistering ? "Registering..." : "Register"}
        </button>

        {error && <span className="error">{error}</span>}
      </form>
      <button className="registerLoginButton" onClick={() => navigate("/login")}>
        Login
      </button>
    </div>
  );
}
