import "./topbar.css"
import { auth } from "../firebase";
import { signInWithPopup, GoogleAuthProvider } from "firebase/auth";

export default function Topbar({ onAddClick }) {
  const signInWithGoogle = async () => {
    const provider = new GoogleAuthProvider();
    try {
      await signInWithPopup(auth, provider);
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div className="topBar">
      <header className="headerTitle">Habits</header>
      <div className="buttonContainer">
        <button className="addButton" onClick={onAddClick}>
          <i className="bi bi-plus-lg"></i>
        </button>
        <button 
          className="loginButton2" 
          onClick={auth.currentUser ? () => auth.signOut() : signInWithGoogle}
        >
          <i className="fa-solid fa-circle-user"></i>
        </button>
      </div>
    </div>
  );
}
