import { useState, useEffect } from "react";
import "./userinfo.css";
import { useAuth } from "../../../../contexts/authContext/AuthProvider";
import { doPasswordChange, doSignOut } from "../../../../firebase/auth";
import { validatePassword } from "../../../../utils/passwordHelpers";

export default function UserInfo({ onSettingsClose }) {
  const { user } = useAuth();
  const [userData, setUserData] = useState(null);
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [isEditing, setIsEditing] = useState(false);
  const [newPassword, setNewPassword] = useState("");
  const [confirmNewPassword, setConfirmNewPassword] = useState("");
  const [isUpdatingPassword, setIsUpdatingPassword] = useState(false);
  const [error, setError] = useState("");
  const [message, setMessage] = useState("");

  // 获取用户数据
  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const response = await fetch(`http://localhost:5001/api/users/${user.uid}`);
        if (!response.ok) {
          throw new Error('Failed to fetch user data');
        }
        const data = await response.json();
        setUserData(data);
        setFirstName(data.firstName);
        setLastName(data.lastName);
      } catch (error) {
        setError("Failed to load user information");
      }
    };

    if (user) {
      fetchUserData();
    }
  }, [user]);

  const handleOverlayClick = (e) => {
    if (e.target.className === 'userInfo') {
      onSettingsClose();
    }
  };

  const handlePasswordChange = async (e) => {
    e.preventDefault();
    if (isUpdatingPassword) return;

    setError("");
    setMessage("");

    if (!newPassword || !confirmNewPassword) {
      setError("Please fill in all fields");
      return;
    }

    if (newPassword !== confirmNewPassword) {
      setError("Passwords do not match");
      return;
    }

    const passwordError = validatePassword(newPassword);
    if (passwordError) {
      setError(passwordError);
      return;
    }

    try {
      setIsUpdatingPassword(true);
      await doPasswordChange(newPassword);
      setMessage("Password updated successfully!");
      setNewPassword("");
      setConfirmNewPassword("");
    } catch (error) {
      setIsUpdatingPassword(false);
      setError("Failed to update password");
    }
  };

  // 更新用户名字
  const handleUpdateName = async () => {
    try {
      const response = await fetch(`http://localhost:5001/api/users/${user.uid}/update-name`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          firstName,
          lastName,
        }),
      });

      if (!response.ok) {
        throw new Error('Failed to update name');
      }

      setMessage("Name updated successfully");
      setIsEditing(false);
      // 更新本地数据
      setUserData(prev => ({
        ...prev,
        firstName,
        lastName,
      }));
    } catch (error) {
      setError("Failed to update name");
    }
  };

  return (
    <div className="userInfo" onClick={handleOverlayClick}>
      <div className="userInfoWrapper">
        <button className="closeButton" onClick={onSettingsClose}>×</button>
        <h1 className="userInfoTitle">User Profile</h1>

        <div className="userInfoItem">
          <label>Username</label>
          <div className="userInfoValue">{userData?.username}</div>
        </div>

        <div className="userInfoItem">
          <label>Name</label>
          {isEditing ? (
            <div className="nameEditForm">
              <input
                type="text"
                value={firstName}
                onChange={(e) => setFirstName(e.target.value)}
                placeholder="First Name"
                className="nameInput"
              />
              <input
                type="text"
                value={lastName}
                onChange={(e) => setLastName(e.target.value)}
                placeholder="Last Name"
                className="nameInput"
              />
              <div className="nameEditButtons">
                <button onClick={handleUpdateName} className="saveButton">Save</button>
                <button onClick={() => setIsEditing(false)} className="cancelButton">Cancel</button>
              </div>
            </div>
          ) : (
            <div className="nameDisplay">
              <span className="userInfoValue">
                {userData ? `${userData.firstName} ${userData.lastName}` : ''}
              </span>
              <button onClick={() => setIsEditing(true)} className="editButton">
                Edit
              </button>
            </div>
          )}
        </div>

        <div className="userInfoItem">
          <label>Email</label>
          <div className="userInfoValue">{user?.email}</div>
        </div>

        <form className="passwordChangeForm" onSubmit={handlePasswordChange}>
          <h2>Change Password</h2>
          <div className="passwordInputContainer">
            <input
              type="password"
              placeholder="New password"
              value={newPassword}
              onChange={(e) => setNewPassword(e.target.value)}
              className="passwordInput"
            />
            <input
              type="password"
              placeholder="Confirm new password"
              value={confirmNewPassword}
              onChange={(e) => setConfirmNewPassword(e.target.value)}
              className="passwordInput"
            />
          </div>
          <button type="submit" className="passwordChangeButton">Update Password</button>
        </form>

        {error && <span className="error">{error}</span>}
        {message && <span className="success">{message}</span>}

        <button 
          className="userLogoutButton" 
          onClick={() => {
            doSignOut();
            onSettingsClose();
          }}
        >
          Logout
        </button>
      </div>
    </div>
  );
}
