import "./createhabit.css";
import { useState } from "react";

export default function CreateHabit({ onCreateHabitClose }) {
  const [habitName, setHabitName] = useState("");
  const [frequency, setFrequency] = useState("daily");
  const [targetAmount, setTargetAmount] = useState("");
  const [targetUnit, setTargetUnit] = useState("times");
  const [reminderEnabled, setReminderEnabled] = useState(false);
  const [reminderMode, setReminderMode] = useState("fixed");
  const [reminderInterval, setReminderInterval] = useState("");
  const [reminderUnit, setReminderUnit] = useState("days");
  const [reminderTime, setReminderTime] = useState("09:00");
  const [startDate, setStartDate] = useState("");
  const [endDate, setEndDate] = useState("");
  const [error, setError] = useState("");

  const handleOverlayClick = (e) => {
    if (e.target.className === 'createHabitOverlay') {
      onCreateHabitClose();
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!habitName) {
      setError("Please enter a habit name");
      return;
    }
    // 处理表单提交
    console.log({
      habitName,
      frequency,
      targetAmount,
      targetUnit,
      reminderEnabled,
      reminderMode,
      reminderInterval,
      reminderUnit,
      reminderTime,
      startDate,
      endDate
    });
  };

  return (
    <div className="createHabitOverlay" onClick={handleOverlayClick}>
      <div className="createHabit">
        <button className="closeButton" onClick={onCreateHabitClose}>×</button>
        <h1 className="createHabitTitle">Create New Habit</h1>

        <form className="createHabitForm" onSubmit={handleSubmit}>
          {/* 习惯名称 */}
          <div className="formGroup">
            <label>Habit Name</label>
            <input
              type="text"
              placeholder="e.g., Running, Reading..."
              value={habitName}
              onChange={(e) => setHabitName(e.target.value)}
            />
          </div>

          {/* 频率 */}
          <div className="formGroup">
            <label>Frequency</label>
            <select value={frequency} onChange={(e) => setFrequency(e.target.value)}>
              <option value="daily">Daily</option>
              <option value="weekly">Weekly</option>
              <option value="monthly">Monthly</option>
            </select>
          </div>

          {/* 目标数量 */}
          <div className="formGroup targetGroup">
            <label>Target</label>
            <div className="targetInputGroup">
              <input
                type="number"
                placeholder="Amount"
                value={targetAmount}
                onChange={(e) => setTargetAmount(e.target.value)}
              />
              <select value={targetUnit} onChange={(e) => setTargetUnit(e.target.value)}>
                <option value="times">times</option>
                <option value="minutes">minutes</option>
                <option value="hours">hours</option>
              </select>
            </div>
          </div>

          {/* 提醒设置 */}
          <div className="formGroup">
            <label className="checkboxLabel">
              <input
                type="checkbox"
                checked={reminderEnabled}
                onChange={(e) => setReminderEnabled(e.target.checked)}
              />
              Enable Reminders
            </label>
          </div>

          {/* 提醒相关设置 */}
          {reminderEnabled && (
            <>
              <div className="formGroup">
                <label>Reminder Mode</label>
                <select value={reminderMode} onChange={(e) => setReminderMode(e.target.value)}>
                  <option value="fixed">Fixed Interval</option>
                  <option value="completion">After Completion</option>
                </select>
              </div>

              <div className="formGroup">
                <label>Reminder Interval</label>
                <div className="reminderInputGroup">
                  <input
                    type="number"
                    placeholder="Interval"
                    value={reminderInterval}
                    onChange={(e) => setReminderInterval(e.target.value)}
                  />
                  <select value={reminderUnit} onChange={(e) => setReminderUnit(e.target.value)}>
                    <option value="hours">Hours</option>
                    <option value="days">Days</option>
                  </select>
                </div>
              </div>

              <div className="formGroup">
                <label>Reminder Time</label>
                <input
                  type="time"
                  value={reminderTime}
                  onChange={(e) => setReminderTime(e.target.value)}
                />
              </div>
            </>
          )}

          {/* 日期设置 */}
          <div className="formGroup dateGroup">
            <div>
              <label>Start Date</label>
              <input
                type="date"
                value={startDate}
                onChange={(e) => setStartDate(e.target.value)}
              />
            </div>
            <div>
              <label>End Date (Optional)</label>
              <input
                type="date"
                value={endDate}
                onChange={(e) => setEndDate(e.target.value)}
              />
            </div>
          </div>

          {error && <div className="error">{error}</div>}

          <div className="buttonGroup">
            <button type="button" className="cancelButton" onClick={onCreateHabitClose}>
              Cancel
            </button>
            <button type="submit" className="submitButton">
              Create Habit
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}
