import "./register.css"
import { useNavigate, Navigate } from "react-router-dom";
import { useAuth } from "../../../contexts/authContext/AuthProvider";
import { createUserWithEmailAndPassword } from "firebase/auth";
import { useState } from "react";
import { auth } from "../../../firebase";
import { doCreateUserWithEmailAndPassword } from "../../../firebase/auth";

export default function Register() {
  const navigate = useNavigate();
  const {userLoggedIn} = useAuth();

  const [email, setEmail] = useState("");
  const [isRegistering, setIsRegistering] = useState(false);
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");

  const validatePassword = (password) => {
    if (password.length < 6) {
      return "Password must be at least 6 characters";
    }
    if (!/\d/.test(password)) {
      return "Password must contain at least one number";
    }
    if (!/[A-Z]/.test(password)) {
      return "Password must contain at least one uppercase letter";
    }
    if (!/[a-z]/.test(password)) {
      return "Password must contain at least one lowercase letter";
    }
    return "";
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");

    if(isRegistering) {
      return;
    }
    setIsRegistering(true);

    // Test if email and password are filled
    if (!email || !password) {
      setError("Please fill in all fields");
      return;
    }
    // Test if password is complex enough
    const passwordError = validatePassword(password);
    if (passwordError) {
      setError(passwordError);
      return;
    }

    try {
      await createUserWithEmailAndPassword(auth, email, password);
      navigate("/");
    } catch (error) {
      switch (error.code) {
        case 'auth/email-already-in-use':
          setError("Email already exists");
          break;
        case 'auth/invalid-email':
          setError("Invalid email format");
          break;
        default:
          setError("Registration failed. Please try again.");
      }
    }
  };

  return (
    <div className="register">
      {userLoggedIn && <Navigate to="/" />}
      <span className="registerTitle">Register</span>
      <form className="registerForm" onSubmit={handleSubmit}>

        <label>Email</label>
        <input 
          className="registerInput" 
          type="email" 
          placeholder="Enter your email..." 
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          autoFocus={true}
        />

        <label>Password</label>
        <input 
          className="registerInput" 
          type="password" 
          placeholder="Enter your password..."
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        {error && <span className="registerError">{error}</span>}

        <button className="registerButton" type="submit">Register</button>
      </form>

      <button className="registerLoginButton" onClick={() => navigate('/login')}>Login</button>
    </div>
  );
}
