import './App.css';
import Home from './components/pages/home/Home';
import CreateHabit from './components/pages/createHabit/CreateHabit';
import { useState } from 'react';

function App() {
  const [showCreateHabit, setShowCreateHabit] = useState(false);

  const handleClose = () => {
    setShowCreateHabit(false);
  };

  return (
    <>
      <Home onAddClick={() => setShowCreateHabit(true)} />
      {showCreateHabit && <CreateHabit onClose={handleClose} />}
    </>
  );
}

export default App;
