import "./createhabit.css";
import { useState } from "react";

export default function CreateHabit({ onCreateHabitClose }) {
  const [frequency, setFrequency] = useState("days");
  const [frequencyAmount, setFrequencyAmount] = useState("1");

  const handleFrequencyChange = (e) => {
    const value = e.target.value;
    setFrequency(value);
    if (value === 'weekly' || value === 'monthly') {
      setFrequencyAmount("1");
    }
  };

  const handleOverlayClick = (e) => {
    if (e.target.className === 'createHabitOverlay') {
      onCreateHabitClose();
    }
  };

  return (
    <div className="createHabitOverlay" onClick={handleOverlayClick}>
      <div className="createHabit">
        <div className="createHabbitTitle">Add a Habit</div>
        <button className="createHabitButton" onClick={onCreateHabitClose}>Back</button>
        <button className="createHabitButton">Submit</button>
        <form className="createHabitForm">
          <div className="createHabitInputGroup">
            <label>I want to</label>
            <input 
              type="text" 
              className="createHabitInput" 
              placeholder="read a book"
              autoFocus={true}
            />
          </div>

          <div className="createHabitInputGroup">
            <input 
              type="number" 
              className="createHabitInput" 
              placeholder="10"
            />
            <select className="createHabitSelect">
              <option value="times">times</option>
              <option value="mins">mins</option>
              <option value="hours">hours</option>
            </select>
          </div>

          <div className="createHabitInputGroup">
            <label>every</label>
            {frequency === 'days' ? (
              <input
                type="number"
                min="1"
                max="365"
                className="createHabitInput"
                style={{ width: '100px' }}
                value={frequencyAmount}
                onChange={(e) => setFrequencyAmount(e.target.value)}
              />
            ) : (
              <input
                type="number"
                value="1"
                className="createHabitInput disabled"
                style={{ width: '100px' }}
                disabled
              />
            )}
            <select 
              className="createHabitSelect"
              value={frequency}
              onChange={handleFrequencyChange}
            >
              <option value="days">days</option>
              <option value="week">week</option>
              <option value="month">month</option>
            </select>
          </div>
        </form>
      </div>
    </div>
  )
}
