import "./createhabit.css";
import { useState } from "react";

export default function CreateHabit({ onCreateHabitClose }) {
  const [frequency, setFrequency] = useState("days");
  const [frequencyAmount, setFrequencyAmount] = useState("1");
  const [reminderEnabled, setReminderEnabled] = useState(false);
  const [reminderMode, setReminderMode] = useState("fixed"); // "fixed" or "completion"
  const [reminderInterval, setReminderInterval] = useState("");
  const [reminderTime, setReminderTime] = useState("09:00");
  const [startDate, setStartDate] = useState("");
  const [endDate, setEndDate] = useState("");

  const handleFrequencyChange = (e) => {
    const value = e.target.value;
    setFrequency(value);
    if (value === 'weekly' || value === 'monthly') {
      setFrequencyAmount("1");
    }
  };

  const handleOverlayClick = (e) => {
    if (e.target.className === 'createHabitOverlay') {
      onCreateHabitClose();
    }
  };

  return (
    <div className="createHabitOverlay" onClick={handleOverlayClick}>
      <div className="createHabit">
        <div className="createHabbitTitle">What's your next goal?</div>
        <button className="createHabitButton" onClick={onCreateHabitClose}>Back</button>
        <button className="createHabitButton">Submit</button>
        <form className="createHabitForm">
          <div className="createHabitInputGroup">
            <label>I want to</label>
            <input 
              type="text" 
              className="createHabitInput" 
              placeholder="read a book"
              autoFocus={true}
            />
          </div>

          <div className="createHabitInputGroup">
            <label>every</label>
            {frequency === 'days' ? (
              <input
                type="number"
                min="1"
                max="365"
                className="createHabitInput short"
                value={frequencyAmount}
                onChange={(e) => setFrequencyAmount(e.target.value)}
              />
            ) : (
              <input
                type="number"
                value="1"
                className="createHabitInput short disabled"
                disabled
              />
            )}
            <select 
              className="createHabitSelect"
              value={frequency}
              onChange={handleFrequencyChange}
            >
              <option value="day">day</option>
              <option value="week">week</option>
              <option value="month">month</option>
            </select>
          </div>

          <div className="createHabitInputGroup">
            <label>for</label>
            <input 
              type="number" 
              className="createHabitInput short"
              placeholder="10"
              min="1"
              max="60"
            />
            <select className="createHabitSelect">
              <option value="times">times</option>
              <option value="mins">mins</option>
              <option value="hours">hours</option>
            </select>
            <select className="createHabitSelect wide">
              <option value="eachtime">each time</option>
              <option value="intotal">in total</option>
            </select>
          </div>

          <div className="formDivider"></div>

          <div className="createHabitInputGroup">
            <label className="checkboxLabel">
              <input
                type="checkbox"
                checked={reminderEnabled}
                onChange={(e) => setReminderEnabled(e.target.checked)}
              />
              Enable Reminders
            </label>
          </div>

          {reminderEnabled && (
            <>
              <div className="createHabitInputGroup">
                <label>Reminder Mode</label>
                <select 
                  className="createHabitSelect"
                  value={reminderMode}
                  onChange={(e) => setReminderMode(e.target.value)}
                >
                  <option value="fixed">Fixed Interval</option>
                  <option value="completion">After Completion</option>
                </select>

                <input
                  type="number"
                  className="createHabitInput short"
                  placeholder="Interval"
                  value={reminderInterval}
                  onChange={(e) => setReminderInterval(e.target.value)}
                  min="1"
                />
                <select className="createHabitSelect">
                  <option value="hours">hours</option>
                  <option value="days">days</option>
                </select>

                <input
                  type="time"
                  className="createHabitInput short"
                  value={reminderTime}
                  onChange={(e) => setReminderTime(e.target.value)}
                />
              </div>
            </>
          )}

          <div className="formDivider"></div>

          <div className="createHabitInputGroup">
            <div className="dateGroup">
              <div>
                <label>Start Date</label>
                <input
                  type="date"
                  className="createHabitInput"
                  value={startDate}
                  onChange={(e) => setStartDate(e.target.value)}
                />
              </div>
              <div>
                <label>End Date (Optional)</label>
                <input
                  type="date"
                  className="createHabitInput"
                  value={endDate}
                  onChange={(e) => setEndDate(e.target.value)}
                />
              </div>
            </div>
          </div>
        </form>
      </div>
    </div>
  )
}
