import "./home.css"

export default function Home() {

    const habits = [
        { id: 1, name: 'Read a book' },
        { id: 2, name: 'Exercise' },
        { id: 3, name: 'Meditate' },
    ];

    // Calculate the current month and date
    // Get the current month name
    const today = new Date();
    const curMonth = today.getMonth();
    const m_names = ['January', 'February', 'March', 
        'April', 'May', 'June', 'July', 
        'August', 'September', 'October', 'November', 'December'];

    // Get this week's dates
    const day = today.getDay();
    const diff = today.getDate() - day + (day === 0 ? -6 : 1); // 调整到周一
    const monday = new Date(today.setDate(diff));
    const weekDays = ['Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat', 'Sun'];
    const weekDates = weekDays.map((_, index) => {
        const date = new Date(monday);
        date.setDate(monday.getDate() + index);
        return date.getDate();
    });
    return (
        <div className="container-fluid p-0">
            <div className="d-flex justify-content-between align-items-center">
              <header className="main-header">Habits</header>
              <button 
                className="btn add-button"
                onClick={() => setShowCreateHabit(true)}
              >
                <i className="bi bi-plus-lg"></i>
              </button>
            </div>

            <div className="content-container">
              <div className="calendar-header">
                <div className="empty-cell"></div>
                <div className="month-row" style={{ gridColumn: '2 / -1' }}>
                  {m_names[curMonth]}
                </div>
              </div>
              <div className="calendar-header days-row">
                <div className="empty-cell"></div>
                {weekDays.map((day, index) => (
                  <div key={day} className="calendar-day">
                    <div className="day-name">{day}</div>
                    <div className="day-date">{weekDates[index]}</div>
                  </div>
                ))}
              </div>

              <div className="habits-grid">
                {habits.map(habit => (
                  <div key={habit.id} className="habit-row">
                    <div className="habit-name">{habit.name}</div>
                    {weekDays.map(day => (
                      <div key={`${habit.id}-${day}`} className="checkbox-cell">
                        <div className="checkbox"></div>
                      </div>
                    ))}
                  </div>
                ))}
              </div>
            </div>
      </div>
  )
}
