import './App.css';

function App() {
  // Placeholders to be deleted later
  const habits = [
    {id:1, name:'Read a book'},
    {id:2, name:'Exercise'},
    {id:3, name:'Meditate'},
  ]

  // Calculate the current month and date
  // Get the current month name
  const today = new Date();
  const curMonth = today.getMonth();
  const m_names = ['January', 'February', 'March', 
    'April', 'May', 'June', 'July', 
    'August', 'September', 'October', 'November', 'December'];

  const day = today.getDay();
  const diff = today.getDate() - day + (day === 0 ? -6 : 1); // 调整到周一
  const monday = new Date(today.setDate(diff));
  const weekDays = ['Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat', 'Sun'];
  const weekDates = weekDays.map((_, index) => {
    const date = new Date(monday);
    date.setDate(monday.getDate() + index);
    return date.getDate();
  });


  return (
    <div>
      {/* header */}
      <div>
        <header>Habits</header>
        <button>
          <i className="bi bi-plus-lg"></i>
        </button>
      </div>

      {/* calendar header */}
      <div>{m_names[curMonth]}</div>


      <div>
        {/* calendar body */}
        {weekDays.map((day,index) => (
          <div>
            <div>{day}</div>
            <div>{weekDates[index]}</div>
          </div>
        ))}
      </div>

    </div>
  );
}

export default App;
